mapper.update:
UPDATE board_cafe
SET viewCount=viewCount+1
WHERE num=#{num}

mapper.select:
SELECT writer,title,orgFileName,saveFileName,fileSize, viewCount, regdate
FROM board_data
WHERE num=#{num}

mapper.update:
UPDATE board_data
SET title=#{title}, orgFileName=#{orgFileName}, saveFileName=#{saveFileName}, fileSize=#{fileSize}
WHERE num=#{num}
